	p.status=2 AND p.enabled=1 AND(p.off_shelf IS NULL OR p.off_shelf >= NOW()) AND s.city = #{cityId,jdbcType=BIGINT}

ORDER BY Rand()

SELECT p.* FROM   product_info p
JOIN store_info s ON p.store_id = s.id

	p.status=2 AND p.enabled=1 AND(p.off_shelf IS NULL OR p.off_shelf >= NOW())

		and (p.name like CONCAT('%',#{keywork,jdbcType=VARCHAR},'%') or
		p.title like
		CONCAT('%',#{keywork,jdbcType=VARCHAR},'%'))


		and p.category_id = #{category,jdbcType=BIGINT}


		AND s.city = #{cityId,jdbcType=BIGINT}


		and s.area = #{area,jdbcType=BIGINT}


		and p.max_use = #{maxUse,jdbcType=INTEGER}


		and p.store_id = #{store,jdbcType=BIGINT}


ORDER BY ${clause}

mapper.delete:
DELETE FROM product_info WHERE
store_id = (SELECT s.id FROM store_info s WHERE s.seller_id = #{sellerId})
AND id = #{productId}

mapper.select:
SELECT p.id AS prodId, p.name AS prodName, Substring_index(p.images, "|", 1) AS imageUrl, s.id AS storeId, s.name AS storeName, s.area AS areaId, a.name AS areaName, p.title AS title, p.category_id AS category, p.prod_type AS prodType, p.unit_price AS unitPrice, p.orig_price AS dePrice, p.sales_num AS saleNum FROM product_info p JOIN store_info s ON p.store_id = s.id JOIN area_info a ON s.area = a.id JOIN category_info c ON p.category_id = c.id WHERE p.status = 2 AND p.enabled=1 AND(p.off_shelf IS NULL OR p.off_shelf >= NOW())
AND p.store_id = #{storeId,jdbcType=BIGINT}
AND (c.id=#{category,jdbcType=BIGINT} OR c.parent_id=#{category,jdbcType=BIGINT})
AND s.city=#{city,jdbcType=BIGINT}
ORDER BY ${column}

SELECT t.* FROM ( SELECT p.id AS prodId, p.name AS prodName, p.title AS title, p.unit_price AS unitPrice, p.orig_price AS dePrice, p.sales_num AS saleNum, p.category_id AS category, p.prod_type AS prodType, Substring_index(p.images, "|", 1) AS imageUrl, p.score_avg AS avgScore, p.level AS level, s.id AS storeId, s.name AS storeName, s.area AS areaId, a.name AS areaName FROM store_info s JOIN product_info p ON s.id = p.store_id JOIN category_info c ON p.category_id = c.id JOIN area_info a ON s.area = a.id WHERE p.status = 2 AND p.enabled = 1
AND (p.name like CONCAT('%',#{keyword},'%') or p.title like CONCAT('%',#{keyword},'%'))
AND s.city=#{cityId,jdbcType=BIGINT}
AND s.area = #{areaId,jdbcType=BIGINT}
AND (c.id=#{category,jdbcType=BIGINT} OR c.parent_id=#{category,jdbcType=BIGINT})
) t WHERE 1=1
ORDER BY ${column}

SELECT t.* FROM ( SELECT p.id AS prodId, p.name AS prodName, p.title AS title, p.unit_price AS unitPrice, p.orig_price AS dePrice, p.sales_num AS saleNum, p.category_id AS category, p.prod_type AS prodType, p.level AS level, Substring_index(p.images, "|", 1) AS imageUrl, p.score_avg AS avgScore, s.id AS storeId, s.name AS storeName, s.city AS cityId, s.area AS areaId, a.name AS areaName, sqrt(( ((#{lngtd,jdbcType=DOUBLE}-s.longitude)*PI()*12656*cos(((#{lattd,jdbcType=DOUBLE}+s.latitude)/2)*PI()/180)/180)* ((#{lngtd,jdbcType=DOUBLE}-s.longitude)*PI()*12656*cos (((#{lattd,jdbcType=DOUBLE}+s.latitude)/2)*PI()/180)/180) )+ (((#{lattd,jdbcType=DOUBLE}-s.latitude)*PI()*12656/180)*((#{lattd,jdbcType=DOUBLE}-s.latitude)*PI()*12656/180))) AS distance FROM store_info s JOIN product_info p ON s.id = p.store_id JOIN area_info a ON s.area = a.id JOIN category_info c ON p.category_id = c.id WHERE p.status = 2 AND p.enabled = 1 AND(p.off_shelf IS NULL OR p.off_shelf >= NOW()) AND s.status = 2 AND s.enabled = 1 AND s.longitude < s.longitude + 2 AND s.longitude > s.longitude - 2 AND s.latitude < s.latitude + 2 AND s.latitude > s.latitude - 2
AND (c.id=#{category,jdbcType=BIGINT} OR c.parent_id=#{category,jdbcType=BIGINT})
) t WHERE 1=1
AND t.distance < #{range,jdbcType=BIGINT}
AND (t.prodName like CONCAT('%',#{keyword},'%') or t.title like CONCAT('%',#{keyword},'%'))
AND t.cityId=#{cityId,jdbcType=BIGINT}
AND t.areaId = #{areaId,jdbcType=BIGINT}
ORDER BY ${column}

